import React, {Component, PropTypes} from 'react'
import {connect} from 'react-redux'

import {actionInstance} from '../actions'

import User from './../components/User'

class ClientInstance extends Component {

  _handleTap() {
    actionInstance('tap', 1)
  }

  _handleEmptyTap() {
  }

  render() {
    const {instance} = this.props
    switch (instance.get('state')) {
      default:
      case 'unjoined':
        return (
          <div className="instance">
            <img src="data:image/png;base64,"/>
          </div>
        )
      case 'joined':
        switch (instance.getIn(['data','status'])) {
          default:
          case 'waiting':
            return (
              <div className="instance">
                <User enabled={false} handleTouchTap={this._handleEmptyTap.bind(this)} handleClick={this._handleEmptyTap.bind(this)} />
              </div>
            )
          case 'started':
            return (
              <div className="instance">
                <User enabled={true} handleTouchTap={this._handleTap.bind(this)} handleClick={this._handleTap.bind(this)} />
              </div>
            )
          case 'stoppped':
            return (
              <div className="instance">
                <h1>It's Over</h1>
              </div>
            )
          }
    }
  }
}

ClientInstance.contextTypes = {
  store: PropTypes.object.isRequired
}

ClientInstance.propTypes = {
  instance: PropTypes.object.isRequired,
  actions : PropTypes.object.isRequired
}

function mapStateToProps(state) {
  return {
    instance: state.instance
  }
}

export default connect(
  mapStateToProps
)(ClientInstance)